    echo "</form>\n";    echo "</div>\n";    unset($ligne);    unset($status);    unset($_SESSION["id"]);  break;}function cherche_droits(){  $x = "";  $result = @mysql_query("select * from tables order by nom asc")or die("erreur ".@$_SESSION["table"]." !");  while ($ligne = mysql_fetch_array($result)){    $x[$ligne["id"]] = $ligne;  }  return $x;}function lien_editer($val){  $temp = "<a title=\"Modifier\" href=\"javascript:voir('popup');ajaxpage(rootdomain+'scripts/inc.popup.php?table=".@$_SESSION["table"]."&acte=formulaire&formulaire=modifier&modifier=".$val[0]."','surpopup');loadobjs();\"><b>".$val[1]."</b>&nbsp;</a>";  return $temp;}function verif_droit_champs($droits,$champs,$table,$droits_champs){  $ok = 0;  while (list(, $test) = @each($droits_champs)){    if($test["nom"] == $champs && $test["droits"] == $droits && $test["lien"] == $table && $test["type"] == "droits"){      $ok = $test;    }  }  return $ok;}function verif_droit_table($droits,$table,$droits_table){  $ok = 0;  while (list(, $test) = @each($droits_table)){    if($test["nom"] == $table && $test["droits"] == $droits && $test["type"] == "droits"){      $ok = $test;    }  }  return $ok;}?>